package workers;

public class Workers {
	
	String name;
	int salary;
	
	public Workers() {
		// TODO Auto-generated constructor stub
	}
	public Workers(String name, int salary) {
		// TODO Auto-generated constructor stub
		this.name = name;
		this.salary = salary;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public int getSalary() {
		return salary;
	}
	public void setSalary(int salary) {
		this.salary = salary;
	}
	
	
}
